CREATE TABLE SUPPLY_ITEM (SKU NUMBER(10) NOT NULL, DESCRIPTION VARCHAR2(2000) NULL, ITEM_NAME VARCHAR2(128) NULL, UNIT_COST NUMBER(19,2) NULL, UNITS VARCHAR2(5) NULL, SUPPLYCATEGORY_CATEGORY_NAME VARCHAR2(128) NULL, PRIMARY KEY (SKU))
CREATE TABLE SUPPLY_CATEGORY (CATEGORY_NAME VARCHAR2(128) NOT NULL, PARENT_CATEGORY_NAME VARCHAR2(128) NULL, PRIMARY KEY (CATEGORY_NAME))
CREATE TABLE ROLE (ID NUMBER(19) NOT NULL, NAME VARCHAR2(40) NULL, PRIMARY KEY (ID))
CREATE TABLE USERS (ID NUMBER(19) NOT NULL, ACTIVE_FLAG VARCHAR2(1) NOT NULL, EMAIL_ADDRESS VARCHAR2(255) NOT NULL, EMPLOYEE_ID VARCHAR2(25) NOT NULL UNIQUE, FIRST_NAME VARCHAR2(75) NOT NULL, LAST_NAME VARCHAR2(75) NOT NULL, CREATED_DATE TIMESTAMP NULL, CREATED_BY NUMBER(19) NULL, UPDATED_DATE TIMESTAMP NULL, UPDATED_BY NUMBER(19) NULL, PRIMARY KEY (ID))
CREATE TABLE USER_ROLE (USER_ID NUMBER(19) NOT NULL, ROLE_ID NUMBER(19) NOT NULL, PRIMARY KEY (USER_ID, ROLE_ID))
ALTER TABLE SUPPLY_ITEM ADD CONSTRAINT SPPLYTMSPPLYCTGORYCATEGORYNAME FOREIGN KEY (SUPPLYCATEGORY_CATEGORY_NAME) REFERENCES SUPPLY_CATEGORY (CATEGORY_NAME)
ALTER TABLE SUPPLY_CATEGORY ADD CONSTRAINT SPPLYCATEGORYPRENTCATEGORYNAME FOREIGN KEY (PARENT_CATEGORY_NAME) REFERENCES SUPPLY_CATEGORY (CATEGORY_NAME)
ALTER TABLE USERS ADD CONSTRAINT FK_USERS_CREATED_BY FOREIGN KEY (CREATED_BY) REFERENCES USERS (ID)
ALTER TABLE USERS ADD CONSTRAINT FK_USERS_UPDATED_BY FOREIGN KEY (UPDATED_BY) REFERENCES USERS (ID)
ALTER TABLE USER_ROLE ADD CONSTRAINT FK_USER_ROLE_USER_ID FOREIGN KEY (USER_ID) REFERENCES USERS (ID)
ALTER TABLE USER_ROLE ADD CONSTRAINT FK_USER_ROLE_ROLE_ID FOREIGN KEY (ROLE_ID) REFERENCES ROLE (ID)
